function initMasonry(){const e=document.querySelector(".masonry-grid");e&&new Masonry(e,{percentPosition:!0})}function initPortfolioSlider(){const e=document.querySelector(".portfolio-slider");if(!e)return;new Swiper(e,{spaceBetween:20,slidesPerView:1,navigation:{nextEl:".slide-next",prevEl:".slide-prev"},pagination:{type:"fraction",el:".slides-counter",renderFraction:function(e,t){return'<span class="'+e+'"></span> / <span class="'+t+'"></span>'}},breakpoints:{576:{slidesPerView:"auto",freeMode:!0}}})}function initOffCanvasNav(){const e=document.querySelectorAll(".off-canvas-toggle");e&&e.forEach(e=>{e.addEventListener("click",function(){document.body.classList.toggle("off-canvas-open")})})}function initTabs(){const e=document.querySelectorAll(".tab-link"),t=document.querySelectorAll(".tab-content");(e||t)&&e.forEach(n=>{["click","mouseover"].forEach(o=>n.addEventListener(o,function(){this.classList.contains("active")||(e.forEach(e=>e.classList.remove("active")),t.forEach(e=>e.classList.remove("active")),n.classList.add("active"),document.getElementById(n.getAttribute("data-tab")).classList.add("active"))},!1))})}function initAccordion(){const e=document.querySelectorAll(".accordion");e&&e.forEach(e=>{e.addEventListener("click",function(){const e=this.nextElementSibling;e&&(this.classList.toggle("open"),e.style.maxHeight=e.style.maxHeight?null:e.scrollHeight+"px")})})}function initSmoothScroll(e=document){e.querySelectorAll(".smoothScroll").forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();const t=this.getAttribute("href").replace("/",""),n=document.querySelector(t);try{n.scrollIntoView({block:"start",behavior:"smooth"})}catch(e){console.error(`Element with the ${t} ID not found`)}})})}function initHelpers(){document.querySelectorAll("a[href='#']").forEach(e=>{e.addEventListener("click",e=>e.preventDefault())})}window.onload=(()=>{initHelpers(),initMasonry(),initPortfolioSlider(),AOS.init()});