function initPhotoSlider(){const e=document.querySelector(".about-photo-slider");if(!e)return;const t=new Swiper(e,{loop:!0,spaceBetween:10,effect:"fade",fadeEffect:{crossFade:!0},pagination:{el:".about-photo-slider + .swiper-pagination",clickable:!0},breakpoints:{576:{spaceBetween:20},992:{spaceBetween:30}}});t.on("slideChange",function(){e.querySelectorAll(".aos-animate").forEach(e=>e.classList.remove("aos-animate"));const n=t.slides[t.activeIndex];setTimeout(()=>{n.querySelectorAll(".aos-init").forEach(e=>e.classList.add("aos-animate"))},150)})}function initProjectSlider(){const e=document.querySelector(".project-hero-slider .swiper");if(!e)return;new Swiper(e,{slidesPerView:"auto",centeredSlides:!0,centeredSlidesBounds:!0,loop:!0,navigation:{nextEl:".project-hero-slider .slide-next",prevEl:".project-hero-slider .slide-prev"},pagination:{type:"fraction",el:".single-project-hero .slides-counter",renderFraction:function(e,t){return'<span class="'+e+'"></span> / <span class="'+t+'"></span>'}},breakpoints:{}})}function initMasonry(){const e=document.querySelector(".masonry-grid");e&&new Masonry(e,{percentPosition:!0})}function initPortfolioSlider(){const e=document.querySelector(".portfolio-slider");if(!e)return;new Swiper(e,{spaceBetween:20,slidesPerView:1,navigation:{nextEl:".slide-next",prevEl:".slide-prev"},pagination:{type:"fraction",el:".slides-counter",renderFraction:function(e,t){return'<span class="'+e+'"></span> / <span class="'+t+'"></span>'}},breakpoints:{576:{slidesPerView:"auto",freeMode:!0}}})}function initOffCanvasNav(){const e=document.querySelectorAll(".off-canvas-toggle");e&&e.forEach(e=>{e.addEventListener("click",function(){document.body.classList.toggle("off-canvas-open")})})}function initPricingTabs(){const e=document.querySelectorAll('.pricing-tabs-section input[name="pricing"]'),t=document.querySelectorAll(".pricing-tabs-section .tab-pane");(e||t)&&e.forEach(e=>{e.addEventListener("change",function(e){t.forEach(e=>{e.classList.remove("active"),e.classList.remove("show"),e.querySelectorAll(".aos-animate").forEach(e=>e.classList.remove("aos-animate"))});const n=document.getElementById(`${e.target.id}-tab`);n.classList.add("active"),n.classList.add("show"),setTimeout(()=>{n.querySelectorAll(".aos-init").forEach(e=>e.classList.add("aos-animate"))},150)},!1)})}function initAccordion(){const e=document.querySelectorAll(".accordion");e&&e.forEach(e=>{e.addEventListener("click",function(){const e=this.nextElementSibling;e&&(this.classList.toggle("open"),e.style.maxHeight=e.style.maxHeight?null:e.scrollHeight+"px")})})}function initSmoothScroll(e=document){e.querySelectorAll(".smoothScroll").forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();const t=this.getAttribute("href").replace("/",""),n=document.querySelector(t);try{n.scrollIntoView({block:"start",behavior:"smooth"})}catch(e){console.error(`Element with the ${t} ID not found`)}})})}function initHelpers(){document.querySelectorAll("a[href='#']").forEach(e=>{e.addEventListener("click",e=>e.preventDefault())})}window.onload=(()=>{initHelpers(),initMasonry(),initPortfolioSlider(),initOffCanvasNav(),initProjectSlider(),AOS.init(),initPricingTabs(),initPhotoSlider()});