function initOffCanvasNav(){const t=document.querySelectorAll(".off-canvas-toggle");t&&t.forEach(t=>{t.addEventListener("click",function(){document.body.classList.toggle("off-canvas-open")})})}function initTabs(){const t=document.querySelectorAll(".tab-link"),e=document.querySelectorAll(".tab-content");(t||e)&&t.forEach(c=>{["click","mouseover"].forEach(o=>c.addEventListener(o,function(){this.classList.contains("active")||(t.forEach(t=>t.classList.remove("active")),e.forEach(t=>t.classList.remove("active")),c.classList.add("active"),document.getElementById(c.getAttribute("data-tab")).classList.add("active"))},!1))})}function initAccordion(){const t=document.querySelectorAll(".accordion");t&&t.forEach(t=>{t.addEventListener("click",function(){const t=this.nextElementSibling;t&&(this.classList.toggle("open"),t.style.maxHeight=t.style.maxHeight?null:t.scrollHeight+"px")})})}function initSmoothScroll(t=document){t.querySelectorAll(".smoothScroll").forEach(t=>{t.addEventListener("click",function(t){t.preventDefault();const e=this.getAttribute("href").replace("/",""),c=document.querySelector(e);try{c.scrollIntoView({block:"start",behavior:"smooth"})}catch(t){console.error(`Element with the ${e} ID not found`)}})})}function initHelpers(){document.querySelectorAll("a[href='#']").forEach(t=>{t.addEventListener("click",t=>t.preventDefault())})}window.onload=(()=>{initHelpers()});